import React, {Component, useState} from 'react';
import values from 'lodash/values';
import Node from "./Node.js";

const data = {
    '/root': {
        path: '/root',
        type: 'folder',
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root/gal': {
        path: '/root/gal',
        type: 'folder',
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: 'file',
        isOpen: false,
        content: 'some test'
    }
}

const items = [
    {
        text: 'Add file',
        type: 'directory',
        onClick: () => {
            onClickAddFile();
        }
    },
    {
        text: 'Add directory',
        type: 'directory',
        onClick: () => {
            onClickAddDirectory();
        }
    },
    {
        text: 'Delete',
        type: 'directory',
        onClick: () => {
            onClickDeleteDirectory();
        }
    },
    {
        text: 'Delete',
        type: 'file',
        onClick: () => {
            onClickDeleteFile();
        }
    },

];

export function CateTree(props) {
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const [nodes, setNodes] = useState(data);

    //const nodes = data;
    const getRootNodes = () => {
        return values(nodes).filter(node => node.isRoot === true);
    }

    const onToggle = (node) => {
        nodes[node.path].isOpen = !node.isOpen;
        setNodes(nodes);
    }

    const getChildNodes = (node) => {
        return node.children ? node.children.map(path => nodes[path]) : [];
    }

    const root = getRootNodes();

    return(
        <div>
            {
                root.map(node => (<Node node={node} getChildren={getChildNodes} level={0} onToggle={onToggle}/>))
            }
        </div>
    )
}

export default CateTree;
