import React, {useEffect, useState} from 'react';
import _ from 'lodash';
import {renameChild, getFatherPath, getOldNodePath, getItemsContextMenu} from '../utills/utills'
import Node from "./Node.js";
import {FILE, FOLDER} from "../utills/constants";
import {useHistory} from 'react-router-dom'
import Tree from "./Tree";
import queryString from 'query-string'
import {Button, Container, Typography} from "@material-ui/core";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import CircularProgress from '@material-ui/core/CircularProgress';
import FormDialog from "./FormDialog";
import AlertDialog from "./AlertDialog";
import { Prompt }  from 'react-router';

const data = {
    '/root': {
        path: '/root',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root2': {
        path: '/root2',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root2/gal2']
    },
    '/root2/gal2': {
        path: '/root2/gal2',
        type: FOLDER,
        isRoot: false,
        isOpen: false,
        children: []
    },
    '/root/gal': {
        path: '/root/gal',
        type: FOLDER,
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: FOLDER,
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: FILE,
        isOpen: false,
    }
}

// should send to tree : setChangeWithoutSaving (in case he change), treeData (the tree which he need to render)
export function TreeContainer(props) {
    const [loading, setLoading] = useState(false);
    const [treeData, setTreeData] = useState(null);
    const [changeWithoutSaving, setChangeWithoutSaving] = useState(false);
    const [allData, setAllData] = useState(data);
    const history = useHistory();



    const getRootNodes = (is_exposed) => {
        return _.filter(_.values(is_exposed ? treeData : allData), (node) => { return node.isRoot === true;});
        //return values(nodes).filter(node => node.isRoot === true);
    }

    const newSearchClicked = () => {
        history.push(`${process.env.PUBLIC_URL}/`);
    }

    const onSave = () => {
        //TODO: should implement
        setChangeWithoutSaving(false);
    }

    const fetchTree = async (id) => {
        //let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree?tree_id=${id}`);
        //return await res.json();
        let new_data = _.filter(_.values(allData), (node) => { return node.path.startsWith(`/${id}/`) || node.path === `/${id}`;});
        //return 3;
        return new_data;
        //return await data.json();
    }

    const onRename = (newName) => {
        if(newName){
            let [old_node, name] = getOldNodePath(newName);
            if(old_node == null || name == null)
                return;
            // in case renaming the root
            let new_node = `/${name}`;
            let nodes_copy = JSON.parse(JSON.stringify(allData));
            if(!nodes_copy[old_node].isRoot){
                let father_path = getFatherPath(newName);
                if(father_path == null){
                    return;
                }
                new_node = `${father_path}/${name}`;
                if (new_node in allData){
                    alert(`There is already a file with this name in folder,\n Please insert different name!`);
                    return;
                }
                nodes_copy[father_path].children = nodes_copy[father_path].children.filter((item) => item !== old_node)
                nodes_copy[father_path].children.push(new_node);
            }
            else{
                // case renaming root name, and already have another root with the specific name
                let root_nodes = getRootNodes(false);
                if (root_nodes.includes(new_node)){
                    alert(`There is already a different root with this name,\n Please insert different name!`);
                    return;
                }
                history.push(`${process.env.PUBLIC_URL}/categorytree?tree_id=${name}`)

            }
            renameChild(nodes_copy, old_node, new_node);
            console.log(`Renaming ${old_node} to ${new_node}`);
            setAllData(nodes_copy);
            setChangeWithoutSaving(true);
        }
    };

    useEffect(() => {
        console.log('sean right');
        window.addEventListener('beforeunload', alertUser)
        window.addEventListener('unload', handleEnd)
        return () => {
            window.removeEventListener('beforeunload', alertUser)
            window.removeEventListener('unload', handleEnd)
            handleEnd()
        }
    }, [])
    const alertUser = e => {
        e.preventDefault()
        e.returnValue = ''
    }
    const handleEnd = () => {
        setChangeWithoutSaving(false);
    }

    useEffect( () => {
        const onFirstRender = async () => {

            setLoading(true);
            let parsed = queryString.parse(window.location.search);
            if(!parsed || !parsed.tree_id){
                alert('problem with getting category tree..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            let tree_data = await fetchTree(parsed.tree_id);
            if(!tree_data){
                alert('problem with getting category tree.\n the id which given is not found..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            //let res = buildTree(tree_data, true);
            setTreeData(tree_data);
            //window.addEventListener('resize', centerTree);
            setLoading(false);
        }
        onFirstRender();
    }, [history, allData]);

    const LoadingComponent = () => (
        <Grid item container  justify={'center'} xs={12} style={{minHeight: 200}} >
            <Grid item xs={12} style={{marginTop: '10px'}}>
                <div style={{display: 'flex', flexDirection:'column', justifyContent: 'center', justifyItems: 'center'}}>
                    <Typography style={{marginBottom: '10px'}} variant="h5" align="center" color="textPrimary" >
                        Loading tree...
                    </Typography>
                    <CircularProgress style={{margin:'auto'}} color={'primary'}/>
                </div>
            </Grid>
        </Grid>
    );
    // TODO: if it doesn't work, change to button or AlertDialog.. (not nested in button)

    return(
        <div>
            <Prompt
                when={!changeWithoutSaving}
                message={() => 'Are u sure to leave page without saving?'} />

            <Container maxWidth={'sm'} /*className={classes.tree}*/>

                <Grid item container justify={'center'} xs={12} md={12} spacing={0} wrap={"nowrap"}>
                    <Tooltip title="Go to select another tree!">
                        <Button style={{marginTop: '10px', marginBottom: '10px', marginRight: '5px'}} variant="contained" color="primary">
                            {
                                changeWithoutSaving ? (
                                        <AlertDialog text={'Change tree'} content={'Are u sure to leave page without saving?'} handleClickOk={newSearchClicked}/>) :
                                    (<div onClick={newSearchClicked}>{'Change tree'}</div>)
                            }
                        </Button>
                    </Tooltip>
                    <Tooltip title="Saving your changes..">
                        <Button style={{marginTop: '10px', marginBottom: '10px', marginLeft: '5px'}} onClick={onSave} variant="contained" color="primary">
                            Save
                        </Button>
                    </Tooltip>
                </Grid>
            </Container>
            <Container maxWidth={'xl'}>
                <Paper elevation={3} style={{marginBottom: '10px'}}>
                    <Grid container justify={'center'}>
                        {loading ? <LoadingComponent/> :
                            <Grid item xs={12} style={{minHeight: 200, marginTop: '15px'}}>
                                    <Tree
                                        nodes={allData}
                                        exposedNodes={treeData}
                                        onChangeTree={setChangeWithoutSaving}
                                        setNodes={setAllData}
                                        onRename={onRename}
                                    />
                            </Grid>
                        }
                    </Grid>
                </Paper>
            </Container>
        </div>

    )
}

export default TreeContainer;
