import React, {Component, useEffect, useState} from 'react';
import values from 'lodash/values';
import Node from "./Node.js";
import {ADD_FILE, ADD_FOLDER, RENAME, DELETE, ADD, ADDFO, RNAME, DEL} from "../utills/helpers";

const data = {
    '/root': {
        path: '/root',
        type: 'folder',
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root/gal': {
        path: '/root/gal',
        type: 'folder',
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: 'folder',
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: 'file',
        isOpen: false,
    }
}



// TODO: should implement this functions
// change node, and also his father (children), can get by short the path by 1
function getItemsContextMenu(){

    const onClickAddFile = (node, name) => {
        // add new node by name, also change father childrens
        console.log('add file')
    };
    const onClickAddFolder = (node, name) => {
        // add new node by name, also change father childrens
        console.log('add directory')

    };
    const onClickDeleteFolder= (node) => {
        // delete node by name, also change father childrens
        console.log('delete directory')

    };
    const onClickDeleteFile = (node) => {
        // delete node by name, also change father childrens
        console.log('delete file')

    };
    const onClickRename = (node, name) => {
        // change path of node, also change father childrens
        console.log('rename file/Folder')

    };
    const onClickCancel = (node, name) => {
        // change path of node, also change father childrens
        console.log('rename file/Folder')

    };

    return [
        {
            text: ADD_FILE,
            type: 'folder',
            needNameInput: true,
            // itemClick: (node, name) => {
            //     onClickAddFile(node, name);
            // }
        },
        {
            text: ADD_FOLDER,
            type: 'folder',
            needNameInput: true,
            // itemClick: (node, name) => {
            //     onClickAddFolder(node, name);
            // }
        },
        {
            text: DELETE,
            type: 'both',
            // itemClick: (node) => {
            //     node.type === 'folder' ? onClickDeleteFolder(node) : onClickDeleteFile(node);
            // }
        },
        {
            text: RENAME,
            type: 'both',
            needNameInput: true,
            // itemClick: (node, name) => {
            //     onClickRename(node, name);
            // }
        }
    ];
}

export function CateTree(props) {
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const [nodes, setNodes] = useState(data);
    const [fileToAdd, setFileToAdd] = useState(null);
    const [folderToAdd, setFolderToAdd] = useState(null);
    const [nameFile, setNameFile] = useState(null);
    const [fileToDelete, setFileToDelete] = useState(null);

    // '/root/ben': {
    //     path: '/root/ben',
    //         type: 'folder',
    //         isOpen: false,
    //         children: []
    // },
    // '/root/gal/read.txt': {
    //     path: '/root/gal/read.txt',
    //         type: 'file',
    //         isOpen: false,
    //         content: 'some test'
    // }
    const onAddFile = (node, name) => {
        if(fileToAdd != null && fileToAdd !== ''){
            let nodes_copy = JSON.parse(JSON.stringify(nodes));
            const regex = RegExp('(.*)\\/\\w+$', 'gm');
            let father_node = regex.exec(fileToAdd)[1];
            if(father_node == null || nodes_copy[father_node].type === 'file'){
                return;
            }
            if (fileToAdd in nodes_copy){
                alert('There is already a file with this name in folder,\n Please insert different name!');
            }
            nodes_copy[father_node].children.push(fileToAdd);
            nodes_copy[fileToAdd] = {
                path: fileToAdd,
                type: 'file',
                isOpen: 'false'
            }
            console.log(`Adding file ${fileToAdd}`);
            setNodes(nodes_copy);
        }
        else{
            alert('Please insert name of file to add!');
        }
    };

    const items = getItemsContextMenu();
    const setters = {
        ADD: setFileToAdd,
        ADDFO: setFolderToAdd,
        RNAME: setNameFile,
        DEL: setFileToDelete,
    }
    useEffect(() => {
        onAddFile();
    }, [fileToAdd])
    useEffect(() => {
        if(folderToAdd){
            let old_nodes = JSON.parse(JSON.stringify(nodes));
            old_nodes[fileToAdd] = {path: folderToAdd, }
            console.log(`Adding folder ${folderToAdd}`);
        }
    }, [folderToAdd])
    useEffect(() => {
        if(nameFile){
            let old_nodes = JSON.parse(JSON.stringify(nodes));
            old_nodes[fileToAdd] = {path: nameFile, }
            console.log(`Renaming file ${nameFile}`);
        }
    }, [nameFile])
    useEffect(() => {
        if(fileToDelete){
            console.log(`Deleting file ${fileToDelete}`);
            let old_nodes = JSON.parse(JSON.stringify(nodes));
            delete old_nodes[fileToDelete];
            setNodes(old_nodes);
        }
    }, [fileToDelete])

    const getRootNodes = () => {
        return values(nodes).filter(node => node.isRoot === true);
    }

    const onToggle = (node) => {
        let nodes_copy = JSON.parse(JSON.stringify(nodes));
        nodes_copy[node.path].isOpen = !node.isOpen;
        setNodes(nodes_copy);
    }

    const getChildNodes = (node) => {
        return node.children ? node.children.map(path => nodes[path]) : [];
    }

    const root = getRootNodes();

    return(
        <div>
            {
                root.map((node) => (<Node node={node} getChildren={getChildNodes} level={1} onToggle={onToggle}
                                        menuItems={items} setters={setters}/>))
            }
        </div>
    )
}

export default CateTree;
