import React, {useState, useRef} from 'react';
import last from 'lodash/last';
import styled from 'styled-components'
import PropTypes from 'prop-types'
import { FaFile, FaFolder, FaFolderOpen, FaChevronDown, FaChevronRight } from 'react-icons/fa'
import values from "lodash/values";


// TODO: gal add some styles, otherwise delete the imports
export function Node(props) {
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const { node, getChildren, level, onToggle } = props;
    const containerRef = useRef(null);

    const getName = (node) => last(node.path.split('/'));
    const onContextMenu = (node) => {
        nodes[node.path].isOpen = !node.isOpen;
        setNodes(nodes);
    }
    let node_chevron = undefined;
    let node_icon = <FaFile />;
    let subtree_node = undefined;

    // in case node is folder, otherwise is must be file
    if(node.type === 'folder'){
        if(node.isOpen){
            node_chevron = <FaChevronDown />;
            node_icon = <FaFolderOpen />;
            subtree_node = getChildren(node).map((child) => (
                <Node {...props} node={child} level={level+1} />
            ));
        }
        else{
            node_chevron = <FaChevronRight />;
            node_icon = <FaFolder />;
        }
    }


    return(
        <React.Fragment>
            <div level={level} type={node.type} onClick={() => onToggle(node)} onContextMenu={() => onContextMenu(node)}>
                <div>
                    { node_chevron }
                </div>
                <div marginRight={10}>
                    { node_icon }
                </div>
                <span>
                    { getName(node) }
                </span>
            </div>
            { subtree_node }
        </React.Fragment>
    )
}

export default Node;
