import React, {useState, useRef, useEffect} from 'react';
import last from 'lodash/last';
import _ from 'lodash';
import styled from 'styled-components'
import PropTypes from 'prop-types'
import { FaFile, FaFolder, FaFolderOpen, FaChevronDown, FaChevronRight } from 'react-icons/fa'
import values from "lodash/values";
import MyContextMenu from "./MyContextMenu";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import FormDialog from "./FormDialog";
import {FOLDER, RENAME} from "../utills/constants"
import AlertDialog from "./AlertDialog";


// TODO: gal add some styles, otherwise delete the imports
export function Node(props) {
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const { node, getChildren, level, onToggle, menuItems } = props;
    // const NodeRef = useRef(null);
    const [showContextMenu, setshowContextMenu] = useState(false);
    const [selectedName, setSelectedName] = useState('');

    const getName = (node) => _.last(node.path.split('/'));
    const onContextMenu = () => {
        setshowContextMenu(true)
    }
    const handleClick = (e) => {
        if (e.type === 'click') {
            onToggle(node);
        } else if (e.type === 'contextmenu') {
            setshowContextMenu(true);
        }
    }

    const type_to_setter = {
        'Add file': props.setters.ADD,
        'Add folder': props.setters.ADDFO,
        'Rename': props.setters.RNAME,
        'Delete': props.setters.DEL
    }
    const handleClickOk = (name, type) => {
        if(type === RENAME){
            type_to_setter[type](`${node.path} ${name}`);
        }
        else{
            type_to_setter[type](`${node.path}/${name}`);
        }
    }
    const handleDeleteClick = () => {
        type_to_setter['Delete'](node.path);
    }

    let node_chevron = null;
    let node_icon = <FaFile />;
    let subtree_node = null;
    // in case node is folder, otherwise is must be file
    if(node.type === 'folder'){
        if(node.isOpen){
            node_chevron = <FaChevronDown />;
            node_icon = <FaFolderOpen />;
            subtree_node = _.filter(getChildren(node), (child) => child).map((child) => (
                <Node {...props} node={child} level={level+1} />
            ));
            // subtree_node = getChildren(node).filter((child) => child).map((child) => (
            //     <Node {...props} node={child} level={level+1} />
            // ));
        }
        else{
            node_chevron = <FaChevronRight />;
            node_icon = <FaFolder />;
        }
    }

    let padding_left = (level * 40) + 'px';
    let items = node.type === 'file' ? menuItems.filter((item) => item.type === 'both') : menuItems;
    items = items.map((item) =>
        <MenuItem >
            {
                item.needNameInput ?
                    (<FormDialog text={item.text} setName={handleClickOk} />) :
                    (
                        node.type === FOLDER ? (<AlertDialog text={item.text} handleClickOk={handleDeleteClick} />) :
                            (<div onClick={handleDeleteClick}>{item.text}</div>)
                    )
            }
        </MenuItem>);
    // each node have unique path
    let unique_id = node.path;
    return(
        <React.Fragment>
            <div style={{paddingLeft: padding_left}} >
                <ContextMenuTrigger id={unique_id}>
                    <div style={{marginRight: '5px'}} onClick={handleClick} onContextMenu={handleClick} >
                        { node_icon }
                        { getName(node) }
                        { node_chevron }
                    </div>
                </ContextMenuTrigger>
                {
                    showContextMenu ? (<ContextMenu hideOnLeave={true} id={unique_id}>{items}</ContextMenu>) : null
                }
            </div>
            { subtree_node }
        </React.Fragment>
    )
}

export default Node;
