import React, {Component, useEffect, useState} from 'react';

export function ContextMenu(props) {
    //const classes = useStyle()
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const [isVisible, setVisible] = useState(false);
    const [xPointer, setX] = useState(0);
    const [yPointer, setY] = useState(0);

    useEffect(() => {
        const parent = props.current;
        if(!parent) return;
        const showMenu = (event) => {
            event.preventDefault();
            setVisible(true);
            setX(event.clientX);
            setY(event.clientY);

        }

        const closeMenu = () => {
            setVisible(false);
        }
        parent.addEventListener('context-menu', showMenu);
        parent.addEventListener('context-click', closeMenu);

        // to avoid memory leaks in case user click a lot
        return function cleanup(){
            parent.removeEventListener('context-menu', showMenu);
            parent.removeEventListener('context-click', closeMenu);

        }
    });

    return isVisible ? (
        <div className='context-menu' style={{top: yPointer, left: xPointer}}>
            Menu
        </div>
    ) : null;
};

export default ContextMenu;
