import MyFooter from "./components/MyFooter";
import CateTree from './components/CateTree'
function App() {
  return (
      <BrowserRouter>
        <ThemeProvider theme={dark_mode? dark_theme : light_theme}>
          <CssBaseline />
          <MyAppBar dark_mode={dark_mode} toggleTheme={toggleTheme}/>
