import React from 'react';

export const loadTree = async (id) => {
    let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree/tree?tree_id=${id}`);
    return await res.json();
}

export const loadData = async () => {
    let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree/load`);
    return await res.json();

}

export const saveData = async (data) => {
    try {
        await fetch(`${process.env.PUBLIC_URL}/calculate_tree/save`, {method: 'PUT', body: data});
    }
    catch (e) {
        console.log(`while try to saving, the error: ${e}`);
    }
    // let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree/save`, {method: 'PUT', body: data});
    // return await res.json().body;

}
