import React, {useEffect, useState} from 'react';
import _ from 'lodash';
import {renameChild, getFatherPath, getOldNodePath, getItemsContextMenu} from '../utills/utills'
import Node from "./Node.js";
import {FILE, FOLDER} from "../utills/constants";
import {useHistory} from 'react-router-dom'
import Tree from "./Tree";
import queryString from 'query-string'
import {Button, Container, Typography} from "@material-ui/core";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import CircularProgress from '@material-ui/core/CircularProgress';

const data = {
    '/root': {
        path: '/root',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root2': {
        path: '/root2',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root2/gal2']
    },
    '/root2/gal2': {
        path: '/root/gal2',
        type: FOLDER,
        isRoot: false,
        isOpen: false,
        children: []
    },
    '/root/gal': {
        path: '/root/gal',
        type: FOLDER,
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: FOLDER,
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: FILE,
        isOpen: false,
    }
}

export function CategoryTree(props) {
    const [loading, setLoading] = useState(false);
    const [treeData, setTreeData] = useState(null);

    const history = useHistory();

    const newSearchClicked = () => {
        history.push(`${process.env.PUBLIC_URL}/`);
    }

    const fetchTree = async (id) => {
        //let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree?treeid=${id}`);
        //return await res.json();
        let new_data = _.filter(_.values(data), (node) => { return node.startsWith(id);});
        return await new_data.json();
        //return await data.json();
    }

    useEffect( () => {
        const onFirstRender = async () => {
            setLoading(true);
            let parsed = queryString.parse(window.location.search);
            if(!parsed || !parsed.id){
                alert('problem with getting category tree..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            let tree_data = await fetchTree(parsed.id);
            if(!tree_data){
                alert('problem with getting category tree.\n the id which given is not found..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            //let res = buildTree(tree_data, true);
            setTreeData(tree_data);
            //window.addEventListener('resize', centerTree);
            setLoading(false);
        }

        onFirstRender();
    }, [history]);

    const LoadingComponent = () => (
        <Grid item container  justify={'center'} xs={12} style={{minHeight: 500}} >
            <Grid item xs={12} style={{marginTop: '10px'}}>
                <div style={{display: 'flex', flexDirection:'column', justifyContent: 'center', justifyItems: 'center'}}>
                    <Typography style={{marginBottom: '10px'}} variant="h5" align="center" color="textPrimary" >
                        Loading tree...
                    </Typography>
                    <CircularProgress style={{margin:'auto'}} color={'primary'}/>
                </div>
            </Grid>
        </Grid>
    );
    

}

export default CategoryTree;
