import React, {useEffect, useState} from 'react';
import {useHistory} from "react-router-dom";
import {makeStyles} from "@material-ui/core/styles";
import {Container} from "@material-ui/core";
import Paper from '@material-ui/core/Paper';
import Grid from "@material-ui/core/Grid";
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from "@material-ui/core/InputLabel";
import Button from '@material-ui/core/Button';
import Tooltip from '@material-ui/core/Tooltip';
import FormDialog from "./FormDialog";
import { MenuItem } from "react-contextmenu";

import _ from "lodash";
import {FILE, FOLDER} from "../utills/constants";
import Node from "./Node";
import Typography from "@material-ui/core/Typography";
import {getFatherPath} from "../utills/utills";

// TODO : understand localStorage, add styles
const data = {
    '/root': {
        path: '/root',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root2': {
        path: '/root2',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root2/gal2']
    },
    '/root2/gal2': {
        path: '/root/gal2',
        type: FOLDER,
        isRoot: false,
        isOpen: false,
        children: []
    },
    '/root/gal': {
        path: '/root/gal',
        type: FOLDER,
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: FOLDER,
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: FILE,
        isOpen: false,
    }
}

const useStyles = makeStyles((theme) => ({
    container: {
        backgroundColor: theme.palette.background.default,
        padding: theme.spacing(4, 0, 3),
    },
    headlines: {
        paddingTop: theme.spacing(1)
    },
    formControl: {
        marginBottom: theme.spacing(1),
        minWidth: 250,
    },
    button:{
        marginBottom: theme.spacing(1)
    }
}));

export function MyLoader(props) {
    const classes = useStyles();
    // TODO : fix inital attirbute
    //const [cateTree, setCateTree] = useState(localStorage.getItem('category_tree') ? localStorage.getItem('category_tree') : initial_cateTree_string);
    const [treeName, setTreeName] = useState(null);
    //const [all_category_trees, setAllCateTree] = useState(localStorage.getItem('all_category_tree') ? localStorage.getItem('all_category_tree') : false);
    //const [catetree_required, setCateTreeRequired] = useState(false);
    //const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [nodes, setNodes] = useState(data);

    const getRootNodes = () => {
        return _.map(_.filter(_.values(nodes), (node) => { return node.isRoot === true;}),(node) => _.last(node.path.split('/')));
        //return values(nodes).filter(node => node.isRoot === true);
    }
    const [allRoots, setAllRoots] = useState(getRootNodes());

    // const fetchWithLoading = async (arg) => {
    //     await setLoading(true);
    //     let res = await fetch(arg);
    //     let formatted = await res.json();
    //     await setLoading(false);
    //     return formatted;
    // }
    //
    // useEffect(() => {
    //     const onEffect = async () => {
    //         if(faculty !== ''){
    //             let res = await fetchWithLoading(`${kdam_url}/all_courses_for_faculty?faculty=${faculty}&is_all_courses=${all_courses}`);
    //             // formatted.push(empty_course_obj); may do it here and not in the backend later
    //             setCourseList(res);
    //         }
    //     }
    //     onEffect();
    // }, [treeName]);

    const onAddRoot = (newFile, is_file, is_root) => {
        if(newFile){
            if (newFile in nodes){
                alert(`There is already a file with this name in folder,\n Please insert different name!`);
                return;
            }
            let nodes_copy = JSON.parse(JSON.stringify(nodes));
            if(!is_root) {
                let father_path = getFatherPath(newFile);
                if (father_path == null || nodes[father_path].type === FILE) {
                    return;
                }
                nodes_copy[father_path].children.push(newFile);
            }

            nodes_copy[newFile] = is_file ? {
                path: newFile,
                type: FILE,
                isOpen: false
            } : {
                path: newFile,
                type: FOLDER,
                isOpen: false,
                children: []
            }
            if(is_root){
                nodes_copy[newFile].isRoot = true;
            }
            console.log(`Adding ${is_file ? FILE : FOLDER} ${newFile}`);
            setNodes(nodes_copy);
        }
    };

    useEffect(() => {
        // TODO: just need to render the component again
    }, [allRoots, treeName])

    const changedCateTreeOption = (name) => {
        setTreeName(name);
        // gal understand what to do with localStorage
        //localStorage.removeItem('category_tree');
        //localStorage.setItem('category_tree', e.target.value);
    }
    const loadClicked = () => {
        if(treeName === '' || treeName == null){
            //setTreeNameRequired(true);
            alert('Please choose category tree first, and then click Load!');
            return;
        }
        history.push(`${process.env.PUBLIC_URL}/categorytree?tree_id=${treeName}`)
    }
    const createClicked = (name, type) => {

        let newRoots = allRoots.slice();
        newRoots.push(name);
        setAllRoots(newRoots);
        // TODO: also need to add it to server json
        onAddRoot(`/${name}`, false, true);
        changedCateTreeOption(name);
    }

    let disabled_bool = /*loading === true || */allRoots.length === 0;

    return(
        <div>
            <Container maxWidth={'md'} >
                <Paper elevation={3} >
                    <Grid container alignItems={'center'} direction={'column'}>
                        <Grid item >
                            <FormControl disabled={disabled_bool} style={{minWidth: 250}} >
                                <InputLabel>Select category tree</InputLabel>
                                <Select
                                    value={treeName}
                                    onChange={(e) => changedCateTreeOption(e.target.value)}>
                                    {
                                        _.map(allRoots, (node) => {
                                            return (<MenuItem value={node}>{node}</MenuItem>)
                                        })
                                    }
                                </Select>
                            </FormControl>
                        </Grid>
                        <Grid item  className={classes.button}>
                            <Tooltip title="Go to selected category!">
                                <Button style={{marginTop: '10px', marginBottom: '10px', marginRight: '5px'}} onClick={loadClicked} variant="contained" color="primary">
                                    Go!
                                </Button>
                            </Tooltip>
                            <Tooltip title="Create new category tree!">
                                <Button style={{marginTop: '10px', marginBottom: '10px', marginLeft: '5px'}} variant="contained" color="primary">
                                    <FormDialog text={'Create'} onClickOk={createClicked} />
                                </Button>
                            </Tooltip>
                        </Grid>
                    </Grid>
                </Paper>
            </Container>
        </div>
    )
}

export default MyLoader;
