        //let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree?tree_id=${id}`);
        //return await res.json();
        let new_data = _.filter(_.values(nodes), (node) => { return node.path.startsWith(`/${id}/`) || node.path === `/${id}`;});
        //return 3;
        return new_data;
        //return await data.json();
