        //let tree_data = await fetchTree(parsed.tree_id);
        if(!tree_data){
            alert('problem with getting category tree.\n the id which given is not found..');
            history.push(`${process.env.PUBLIC_URL}/`);
            return;
        }
        onLoading(false);
        //let res = buildTree(tree_data, true);
        return tree_data;
    }

