    //     onAdd(fileToAdd, true);
    //     //onChangeTree(true);
    //
    // }, [fileToAdd])
    // useEffect(() => {
    //     if(folderToAdd === null){
    //         return;
    //     }
    //     onAdd(folderToAdd, false);
    //     //onChangeTree(true);
    //
    // }, [folderToAdd])
    // useEffect(() => {
