import React, {Component, useEffect, useState} from 'react';

export function MyContextMenu(props) {
    //const classes = useStyle()
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const [isVisible, setVisible] = useState(false);
    const [xPointer, setX] = useState(0);
    const [yPointer, setY] = useState(0);

    useEffect(() => {
        console.log(props)
        const {node, parent} = props;
        const current = parent.current;
        if(!current) return;

        const showMenu = (event) => {
            event.preventDefault();
            setVisible(true);
            setX(event.clientX);
            setY(event.clientY);
            console.log('i was hereeeee')
        }
        const closeMenu = () => {
            setVisible(false);
            console.log('i was hereeeee')

        }
        current.addEventListener('contextmenu', showMenu);
        current.addEventListener('contextclick', closeMenu);

        // to avoid memory leaks in case user click a lot
        return function cleanup(){
            current.removeEventListener('context-menu', showMenu);
            current.removeEventListener('context-click', closeMenu);

        }
    });

    return isVisible ? (
        <div className='context-menu' style={{top: yPointer, left: xPointer}}>
            menu
        </div>
    ) : null;
};

export default MyContextMenu;
