import React, {useEffect, useState} from 'react';
import {useHistory} from "react-router-dom";
import {Container} from "@material-ui/core";
import Paper from '@material-ui/core/Paper';
import Typography from "@material-ui/core/Typography";
import MenuItem from '@material-ui/core/MenuItem';
import Autocomplete from '@material-ui/lab/Autocomplete';
import TextField from '@material-ui/core/TextField';
import Grid from "@material-ui/core/Grid";
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from "@material-ui/core/InputLabel";
import Button from '@material-ui/core/Button';
import FormDialog from "./FormDialog";
import _ from "lodash";

// TODO : understand localStorage, add styles
const data = {
    '/root': {
        path: '/root',
        type: 'folder',
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root2': {
        path: '/root2',
        type: 'folder',
        isRoot: true,
        isOpen: false,
        children: ['/root2/gal2']
    },
    '/root2/gal2': {
        path: '/root/gal2',
        type: 'folder',
        isRoot: false,
        isOpen: false,
        children: []
    },
    '/root/gal': {
        path: '/root/gal',
        type: 'folder',
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: 'folder',
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: 'file',
        isOpen: false,
    }
}


export function MyLoader(props) {
    const initial_cateTree_string = 'Please choose specific tree';
    //const [cateTree, setCateTree] = useState(localStorage.getItem('category_tree') ? localStorage.getItem('category_tree') : initial_cateTree_string);
    const [cateTree, setCateTree] = useState(initial_cateTree_string);
    //const [all_category_trees, setAllCateTree] = useState(localStorage.getItem('all_category_tree') ? localStorage.getItem('all_category_tree') : false);
    //const [catetree_required, setCateTreeRequired] = useState(false);
    //const [loading, setLoading] = useState(false);
    const history = useHistory();
    const [nodes, setNodes] = useState(data);

    const getRootNodes = () => {
        return _.filter(_.values(nodes), (node) => { return node.isRoot === true;});
        //return values(nodes).filter(node => node.isRoot === true);
    }
    const [allRoots, setAllRoots] = useState(getRootNodes());

    // const fetchWithLoading = async (arg) => {
    //     await setLoading(true);
    //     let res = await fetch(arg);
    //     let formatted = await res.json();
    //     await setLoading(false);
    //     return formatted;
    // }
    //
    // useEffect(() => {
    //     const onEffect = async () => {
    //         if(faculty !== ''){
    //             let res = await fetchWithLoading(`${kdam_url}/all_courses_for_faculty?faculty=${faculty}&is_all_courses=${all_courses}`);
    //             // formatted.push(empty_course_obj); may do it here and not in the backend later
    //             setCourseList(res);
    //         }
    //     }
    //     onEffect();
    // }, [cateTree]);

    useEffect(() => {
        // TODO: just need to render the component again
    }, [allRoots, cateTree])

    const changedCateTreeOption = (e) => {
        setCateTree(e.target.value);
        // gal understand what to do with localStorage
        //localStorage.removeItem('category_tree');
        //localStorage.setItem('category_tree', e.target.value);
    }

    const loadClicked = () => {
        if(cateTree === ''){
            //setCateTreeRequired(true);
            alert('Please choose category tree first, and then click Load!');
            return;
        }
        history.push(`${process.env.PUBLIC_URL}/categorytree?id=${cateTree}`)
    }

    const createClicked = (name, type) => {
        let newRoots = allRoots.slice();
        newRoots.push(name);
        setAllRoots(newRoots);
        // TODO: also need to add it to server json
        changedCateTreeOption(name);
    }

    let disabled_bool = /*loading === true || */allRoots.length === 0;

    return(
        <div>
            <Container maxWidth={'sm'}>
                <Paper elevation={3} >
                    <Grid container alignItems={'center'} direction={'column'}>
                        <Grid item>
                            <FormControl /*error={catetree_required}*/ disabled={disabled_bool}>
                                <InputLabel>Select category tree</InputLabel>
                                <Select
                                    value={cateTree}
                                    onChange={changedCateTreeOption}>
                                </Select>
                            </FormControl>
                        </Grid>
                        <Grid item>
                            <Button title={'Load the chosen category tree option'} onClick={loadClicked} variant="contained" color="primary">
                                Load
                            </Button>
                        </Grid>
                        <Grid item>
                            <Button title={'Create new category tree'} variant="contained" color="primary">
                                <FormDialog text={'Create'} onClickOk={createClicked} />
                            </Button>
                        </Grid>
                    </Grid>
                </Paper>
            </Container>
        </div>
    )
}

export default MyLoader;
