import React, {useEffect, useState} from 'react';
import _ from 'lodash';
import {getItemsContextMenu} from '../utills/utills'
import Node from "./Node.js";
import {Container} from "@material-ui/core";
import {useHistory} from 'react-router-dom'
import queryString from "query-string";
import {loadData, loadTree} from '../Api'

export function Tree(props) {
    const { nodes, setNodes, onRename, onDelete, onAdd, onLoading, rootName } = props;
    const history = useHistory();
    const [exposedNodes, setExposedNodes] = useState({});
    const [rootNodes, setRootNodes] = useState([]);

    // TODO: in future will be request api and not data (get exposed data data - getTree(id))


    const getRootNodes = (exposed) => {
        return _.filter(_.values(exposed), (node) => { return node.isRoot === true;});
    }

    useEffect(() => {
        console.log('asdasdasaaaaaaa')
        const fetchTree = async (id) => {
            console.log('789')

            return loadTree(id);
        }
        const getExposedTree = async () => {
            onLoading(true);
            let parsed = queryString.parse(window.location.search);
            if(!parsed || !parsed.id){
                alert('problem with getting category tree..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            let tree_data = await fetchTree(parsed.id);
            console.log('456')

            if(!tree_data){
                alert('problem with getting category tree.\n the id which given is not found..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            onLoading(false);
            return tree_data;
        }

        let exposed = getExposedTree();
        console.log('123')

        setExposedNodes(exposed);
        setRootNodes(getRootNodes(exposed));
    }, [])

    // useEffect(() => {
    //     const onRender = async () => {
    //         let res = await loadData();
    //         setNodes(res);
    //         setAllRoots(getRootNodesWithParam(res));
    //     }
    //     onRender();
    // }, [])

    const items = getItemsContextMenu();
    const setters = {
        ADD: onAdd,
        RNAME: onRename,
        DEL: onDelete,
    }

    const onToggle = (node) => {
        let nodes_copy = JSON.parse(JSON.stringify(nodes));
        nodes_copy[node.path].isOpen = !nodes_copy[node.path].isOpen;
        setNodes(nodes_copy);
    }
    const getChildNodes = (node) => {
        return _.map(node.children, (path) => {
            return nodes[path];
        });
    }

    //const root = getRootNodes();

    return(
        <Container maxWidth={'md'} >
            {
                _.map(rootNodes, (node) => {
                    return (<Node node={node} getChildren={getChildNodes} level={1} onToggle={onToggle}
                                  menuItems={items} setters={setters} />)
                })
            }
        </Container>
    )
}

export default Tree;
