const localHost = `http://localhost:3232`

export const loadTree = async (id) => {
    //let res = await fetch(`${localHost}/calculate_tree/tree?tree_id=${id}`);
    //return await res.json();
    let url = `${localHost}/calculate_tree/tree?id=${id}`
    fetch(url).then(response => {
                if (response.ok) {
                    response.json().then(json => {
                        console.log(json);
                    });
                }
    });
}

export const loadData = async () => {
    let url = `${localHost}/calculate_tree/load`;
    fetch(url).then(response => {
        if (response.ok) {
            response.json().then(json => {
                console.log(json);
            });
        }
    });
    // let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree/load`);
    // return await res.json();
}

export const saveData = async (data) => {
    try {
        let url = `${localHost}/calculate_tree/save`;
        await fetch(url, {method: 'PUT', body: data});
    }
    catch (e) {
        console.log(`while try to saving, the error: ${e}`);
    }
    // let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree/save`, {method: 'PUT', body: data});
    // return await res.json().body;

}
