import React, {Component, useEffect, useState} from 'react';
import values from 'lodash/values';
import last from 'lodash/last';

import Node from "./Node.js";
import {ADD_FILE, ADD_FOLDER, RENAME, DELETE, ADD, ADDFO, RNAME, DEL, FILE, FOLDER} from "../utills/constants";

const data = {
    '/root': {
        path: '/root',
        type: 'folder',
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root/gal': {
        path: '/root/gal',
        type: 'folder',
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: 'folder',
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: 'file',
        isOpen: false,
    }
}

function getItemsContextMenu(){
    return [
        {
            text: ADD_FILE,
            type: 'folder',
            needNameInput: true,
        },
        {
            text: ADD_FOLDER,
            type: 'folder',
            needNameInput: true,
        },
        {
            text: DELETE,
            type: 'both',
        },
        {
            text: RENAME,
            type: 'both',
            needNameInput: true,
        }
    ];
}

export function CateTree(props) {
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const [nodes, setNodes] = useState(data);
    const [fileToAdd, setFileToAdd] = useState(null);
    const [folderToAdd, setFolderToAdd] = useState(null);
    const [newNameFile, setNameFile] = useState(null);
    const [fileToDelete, setFileToDelete] = useState(null);

    const getFatherPath = (path) => {
        const regex = RegExp('(.*)\\/.+$', 'gm');
        let father_node = regex.exec(path) || null;
        return father_node != null ? father_node[1] : null;
    }
    const getOldNodePath = (path) => {
        const regex = RegExp('(.*)\\s(.+)$', 'gm');
        let node = regex.exec(path) || null;
        return node != null ? [node[1], node[2]] : [null, null];
    }
    // /gal/gal1 yoray
    const onRename = (newName) => {
        if(newName){
            let [old_node, name] = getOldNodePath(newName);
            if(old_node == null || name == null)
                return;
            // in case renaming the root
            let new_node = `/${name}`;
            let nodes_copy = JSON.parse(JSON.stringify(nodes));
            if(!nodes_copy[old_node].isRoot){
                let father_path = getFatherPath(newName);
                if(father_path == null){
                    return;
                }
                new_node = `${father_path}/${name}`;
                if (new_node in nodes){
                    alert(`There is already a file with this name in folder,\n Please insert different name!`);
                    return;
                }
                nodes_copy[father_path].children = nodes_copy[father_path].children.filter((item) => item !== old_node)
                nodes_copy[father_path].children.push(new_node);
            }
            else{
                // case renaming root name, and already have another root with the specific name
                let root_nodes = getRootNodes();
                if (root_nodes.includes(new_node)){
                    alert(`There is already a different root with this name,\n Please insert different name!`);
                    return;
                }
            }
            //let new_children = nodes_copy[old_node].children.map( (child) => { child.replace(old_node, new_node)});
            if(nodes_copy[old_node].children.length > 0){
                renameChild(nodes_copy, old_node, new_node);
            }
            let new_children = [];
            // nodes_copy[old_node].children.forEach( (child) => {
            //     let newChild = child.replace(old_node, new_node);
            //     renameChild(nodes_copy, old_node, newChild);
            //     new_children.push(newChild);
            // })
            nodes_copy[new_node] = {
                ...nodes_copy[old_node],
                children: new_children,
                path: new_node,
            }
            delete nodes_copy[old_node];
            console.log(`Renaming ${old_node} to ${new_node}`);
            setNodes(nodes_copy);
        }
    };
    const onDelete = (nodes_copy, file) => {
        if(file){
            if(nodes_copy[file].type === FOLDER){
                nodes_copy[fileToDelete].children.forEach( (child) =>{
                    onDelete(nodes_copy, nodes_copy[child].path);
                })
            }
            if(!nodes_copy[file].isRoot){
                let father_path = getFatherPath(file);
                if(father_path == null || nodes[father_path].type === FILE){
                    return;
                }
                nodes_copy[father_path].children = nodes_copy[father_path].children.filter((item) => item !== file);
            }

            delete nodes_copy[file];
        }
    }
    const onAdd = (newFile, is_file) => {
        if(newFile){
            if (newFile in nodes){
                alert(`There is already a file with this name in folder,\n Please insert different name!`);
                return;
            }
            let nodes_copy = JSON.parse(JSON.stringify(nodes));
            let father_path = getFatherPath(newFile);
            if(father_path == null || nodes[father_path].type === 'file'){
                return;
            }
            nodes_copy[father_path].children.push(newFile);

            nodes_copy[newFile] = is_file ? {
                path: newFile,
                type: 'file',
                isOpen: false
            } : {
                path: newFile,
                type: 'folder',
                isOpen: false,
                children: []
            }
            console.log(`Adding ${is_file ? 'file' : 'folder'} ${newFile}`);
            setNodes(nodes_copy);
        }
    };
    // /gal
        // /gal/gal2
            // /gal/gal2/gal3

    // /root
        // /root/gal2
        // /root/gal2/gal3

    const renameChild = (nodes_copy, old_name, new_name) => {
        nodes_copy[old_name].children.forEach( (child) => {
            renameChild(nodes_copy, child, `${new_name}/${last(child.split('/'))}`);
        });
        nodes_copy[old_name].children = nodes_copy[old_name].children.map( (child) => {
            return child.replace(old_name, new_name);
        });




    }
    const items = getItemsContextMenu();
    const setters = {
        ADD: setFileToAdd,
        ADDFO: setFolderToAdd,
        RNAME: setNameFile,
        DEL: setFileToDelete,
    }

    useEffect(() => {
        onAdd(fileToAdd, true);
    }, [fileToAdd])
    useEffect(() => {
        onAdd(folderToAdd, false);
    }, [folderToAdd])
    useEffect(() => {
        onRename(newNameFile);
    }, [newNameFile])
    useEffect(() => {
        let nodes_copy = JSON.parse(JSON.stringify(nodes));
        onDelete(nodes_copy, fileToDelete);
        // gal should check it
        setNodes(nodes_copy);
        console.log(`Deleting file ${fileToDelete}`);
    }, [fileToDelete])

    const getRootNodes = () => {
        return values(nodes).filter(node => node.isRoot === true);
    }
    const onToggle = (node) => {
        let nodes_copy = JSON.parse(JSON.stringify(nodes));
        nodes_copy[node.path].isOpen = !node.isOpen;
        setNodes(nodes_copy);
    }
    const getChildNodes = (node) => {
        return node.children ? node.children.map(path => nodes[path]) : [];
    }
    const root = getRootNodes();

    return(
        <div>
            {
                root.map((node) => (<Node node={node} getChildren={getChildNodes} level={1} onToggle={onToggle}
                                        menuItems={items} setters={setters}/>))
            }
        </div>
    )
}

export default CateTree;
