import React, {useEffect, useState} from 'react';
import _ from 'lodash';
import {renameChild, getFatherPath, getOldNodePath, getItemsContextMenu} from '../utills/utills'
import Node from "./Node.js";
import {FILE, FOLDER} from "../utills/constants";
import {useHistory} from 'react-router-dom'
import Tree from "./Tree";
import queryString from 'query-string'



const data = {
    '/root': {
        path: '/root',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root2': {
        path: '/root2',
        type: FOLDER,
        isRoot: true,
        isOpen: false,
        children: ['/root2/gal2']
    },
    '/root2/gal2': {
        path: '/root/gal2',
        type: FOLDER,
        isRoot: false,
        isOpen: false,
        children: []
    },
    '/root/gal': {
        path: '/root/gal',
        type: FOLDER,
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: FOLDER,
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: FILE,
        isOpen: false,
    }
}

export function CategoryTree(props) {
    const [loading, setLoading] = useState(false);

    const history = useHistory();

    const newSearchClicked = () => {
        history.push(`${process.env.PUBLIC_URL}/`);
    }

    const fetchTree = async (id) => {
        //let res = await fetch(`${process.env.PUBLIC_URL}/calculate_tree?treeid=${id}`);
        //return await res.json();
        let new_data = _.filter(_.values(data), (node) => { return node.isRoot === true;});

        //return await data.json();
    }

    useEffect( () => {
        const onFirstRender = async () => {
            setLoading(true);
            let parsed = queryString.parse(window.location.search);
            if(!parsed || !parsed.id){
                alert('problem with getting category tree..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            let tree_data = await fetchTree(parsed.id);
            if(!tree_data){
                alert('problem with getting category tree.\n the tree is not found..');
                history.push(`${process.env.PUBLIC_URL}/`);
                return;
            }
            let res = buildTree(tree_data, true);
            setTreeData(res);
            window.addEventListener('resize', centerTree);
            setLoading(false);
        }

        onFirstRender();
    }, [history]);

}

export default CategoryTree;
