import React, {Component, useState} from 'react';
import values from 'lodash/values';
import Node from "./Node.js";

const data = {
    '/root': {
        path: '/root',
        type: 'folder',
        isRoot: true,
        isOpen: false,
        children: ['/root/gal', '/root/ben']
    },
    '/root/gal': {
        path: '/root/gal',
        type: 'folder',
        isOpen: false,
        children: ['/root/gal/read.txt']
    },
    '/root/ben': {
        path: '/root/ben',
        type: 'folder',
        isOpen: false,
        children: []
    },
    '/root/gal/read.txt': {
        path: '/root/gal/read.txt',
        type: 'file',
        isOpen: false,
        content: 'some test'
    }
}

// TODO: should implement this functions
// change node, and also his father (children), can get by short the path by 1
function getItemsContextMenu(){
    const onClickAddFile = (node, name) => {
        // add new node by name, also change father childrens
        console.log('add file')
    };
    const onClickAddFolder = (node, name) => {
        // add new node by name, also change father childrens
        console.log('add directory')

    };
    const onClickDeleteFolder= (node) => {
        // delete node by name, also change father childrens
        console.log('delete directory')

    };
    const onClickDeleteFile = (node) => {
        // delete node by name, also change father childrens
        console.log('delete file')

    };
    const onClickRename = (node, name) => {
        // change path of node, also change father childrens
        console.log('rename file/Folder')

    };
    const onClickCancel = (node, name) => {
        // change path of node, also change father childrens
        console.log('rename file/Folder')

    };

    return [
        {
            text: 'Add file',
            type: 'file',
            onClick: (node, name) => {
                onClickAddFile(node, name);
            }
        },
        {
            text: 'Add Folder',
            type: 'folder',
            onClick: (node, name) => {
                onClickAddFolder(node, name);
            }
        },
        {
            text: 'Delete',
            type: 'folder',
            onClick: (node) => {
                onClickDeleteFolder(node);
            }
        },
        {
            text: 'Delete',
            type: 'file',
            onClick: (node) => {
                onClickDeleteFile(node);
            }
        },
        {
            text: 'Rename',
            type: 'file',
            onClick: (node, name) => {
                onClickRename(node, name);
            }
        },
        {
            text: 'Cancel',
            type: 'file',
            onClick: () => {
                //onClickCancel();
                return;
            }
        }
    ];
}

export function CateTree(props) {
    //const [nodes, setNodes] = useState(localStorage.getItem('nodes') ? localStorage.getItem('nodes') : '');
    const [nodes, setNodes] = useState(data);
    const items = getItemsContextMenu();
    //const nodes = data;
    const getRootNodes = () => {
        return values(nodes).filter(node => node.isRoot === true);
    }

    const onToggle = (node) => {
        let nodes_copy = JSON.parse(JSON.stringify(nodes));
        nodes_copy[node.path].isOpen = !node.isOpen;
        console.log(nodes_copy);
        setNodes(nodes_copy);
    }

    const getChildNodes = (node) => {
        return node.children ? node.children.map(path => nodes[path]) : [];
    }

    const root = getRootNodes();

    console.log(root);
    return(
        <div>
            {
                root.map(node => (<Node node={node} getChildren={getChildNodes} level={1} onToggle={onToggle}
                                        menuItems={items}/>))
            }
        </div>
    )
}

export default CateTree;
